mapper.select:
select user_id, username, password, create_time, salt, login_time, logout_time, last_ip,status,des,avatar
from t_sys_user
where user_id = #{userId,jdbcType=INTEGER}

select u.* from t_sys_user u inner join t_sys_org_user o on u.user_id = o.user_id
u.username like CONCAT('%',#{username},'%')
and u.status = #{status}
and o.org_id= #{orgId}
group by u.user_id ORDER BY u.user_id DESC
